import { Injectable } from '@angular/core';
import { Order } from '../shared/models/Order';
import { HttpClient } from '@angular/common/http';
import { ORDER_CREATE_URL } from '../shared/constants/api-urls';

@Injectable({
  providedIn: 'root'
})
export class OrderService {


  constructor(
    private _http: HttpClient,
  ) { }

  create(order: Order) {
    const user = localStorage.getItem('user'); // Get user from localStorage
    if (!user) {
        console.error("User is missing. Please log in.");
        return; // or throw an error
    }

    const parsedUser = JSON.parse(user); // Parse the user object
    const token = parsedUser.token; // Access the token

    if (!token) {
        console.error("Token is missing. User may not be authenticated.");
        return; // or throw an error
    }

    return this._http.post<Order>(ORDER_CREATE_URL, order, {
        headers: {
            'token': token // Include token here
        }
    });
}

}
